import 'package:flutter/material.dart';import 'package:flutter_riverpod/flutter_riverpod.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:restaurant/framework/controllers/restaurant_details/restaurant_details_controller.dart';import 'package:restaurant/framework/repository/home/model/restaurant.dart';import 'package:restaurant/ui/restaurant_details/mobile/helper/make_reservation_button.dart';import 'package:restaurant/ui/restaurant_details/mobile/helper/restaurant_detail_photos.dart';import 'package:restaurant/ui/restaurant_details/mobile/helper/restaurant_details_body.dart';import 'package:restaurant/ui/restaurant_details/mobile/helper/restaurant_details_card.dart';import 'package:restaurant/ui/utils/helpers/base.dart';import 'package:restaurant/ui/utils/widgets/restaurant_details_tab_bar.dart';import 'package:restaurant/ui/restaurant_details/mobile/helper/restaurant_details_reviews.dart';import 'package:restaurant/ui/restaurant_details/mobile/helper/restaurant_details_top_widget.dart';import 'package:restaurant/ui/utils/const/app_strings.dart';import 'package:restaurant/ui/utils/theme/app_colors.dart';class RestaurantDetailsMobile extends StatelessWidget with BaseStatelessWidget{  const RestaurantDetailsMobile({super.key, required this.restaurant});  final Restaurant restaurant;  @override  Widget buildPage(BuildContext context) {    return Scaffold(      backgroundColor: AppColors.white,      body: SingleChildScrollView(        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            RestaurantDetailsTopWidget(restaurant: restaurant),            RestaurantDetailsCard(restaurant: restaurant),            MakeReservationButton(              restaurant: restaurant,            ),            const RestaurantDetailsBody(),            Consumer(              builder: (BuildContext context, WidgetRef ref, Widget? child) {                final restaurantWatch = ref.watch(restaurantDetailsController);                return RestaurantDetailsTabBar(                  list: AppString.restaurantDetailsTabs,                  onPressed: (int index) {                    restaurantWatch.jumpToPage(index);                  },                );              },            ),            Consumer(              builder: (BuildContext context, WidgetRef ref, Widget? child) {                final restaurantWatch = ref.watch(restaurantDetailsController);                return restaurantWatch.selectedPage == 2                    ? const RestaurantDetailPhotos()                    : restaurantWatch.selectedPage == 0                        ? const RestaurantDetailsReview()                        : Container(                            height: 300.h,                          );              },            ),          ],        ),      ),    );  }}